void printDati(){
    mvprintw(5, 19, "%s", "1"); //Livello
    mvprintw(6, 19, "%s", "000"); //Score
    mvprintw(5, 133, "%s", "@ @ @"); //Vite
    mvprintw(6, 133, "%s", "00:00");//Tempo
    mvprintw(7, 133, "%s", "0");//Frutta
    mvprintw(8, 133, "%s", "0");//Kills

}